    /*
    while select costDistributionTmp
    {
    info(strFmt('%1-%2-%3-%4',costDistributionTmp.DimensionValue1,costDistributionTmp.DimensionValue2,costDistributionTmp.DimensionValue3,costDistributionTmp.DimensionValue4));
