public void init()
{
    if(element.args().record() is SPYBenefitJournalTable)
        benefitJournalTableCaller = element.args().record() as SPYBenefitJournalTable;

    super();



    element.setEnable();
}

public SPYPayrollParameterState payrollParameterState()
{
    container                           workerContainer;
    SPYBenefitJournalTrans              benefitJournalTrans;
    SPYPayrollParameterState            payrollParameterState = new SPYPayrollParameterState();
    container                           benefitLedgerCon,payrollLedgerCon;
    SPYBenefitLedger                    benefitLedger;
    SPYPayrollLedger                    payrollLedger;

    while select worker from benefitJournalTrans group by worker
        where benefitJournalTrans.BenefitJournal == SPYBenefitJournalTable.RecId
    {
        workerContainer = conPoke(workerContainer,conLen(workerContainer)+1,benefitJournalTrans.Worker);
    }
    while select RecId from benefitLedger
        join RecId from benefitJournalTrans
        where benefitJournalTrans.RecId == benefitLedger.BenefitJournalTrans &&
              benefitJournalTrans.BenefitJournal == SPYBenefitJournalTable.RecId
    {
        benefitLedgerCon = conpoke(benefitLedgerCon,conlen(benefitLedgerCon) + 1,benefitLedger.RecId);
    }
    while select RecId from payrollLedger
        join RecId from benefitJournalTrans
        where benefitJournalTrans.RecId == payrollLedger.BenefitJournalTrans &&
              benefitJournalTrans.BenefitJournal == SPYBenefitJournalTable.RecId
    {
        payrollLedgerCon = conpoke(payrollLedgerCon,conlen(payrollLedgerCon) + 1,payrollLedger.RecId);
    }
    payrollParameterState.parmBenefitLedgerCon(benefitLedgerCon);
    payrollParameterState.parmPayrollLedgerCon(payrollLedgerCon);
    payrollParameterState.parmWorkerContainer(workerContainer);
    payrollParameterState.parmCalledFromJournal(true);
    payrollParameterState.parmSourceDocument(SPYBenefitJournalTable.TableId);
    payrollParameterState.parmSourceDocumentRef(SPYBenefitJournalTable.RecId);
    return payrollParameterState;
}

void setEnable()
{
    boolean     isWorkflowEnabled = WorkflowTable::findActiveTemplate(workflowtypestr(SPYBenefitJournal)) ? true : false;
    boolean     checkEditable = ((SPYBenefitJournalTable.WorkflowStatus == SPYWorkflowStatus::Created ||
                                SPYBenefitJournalTable.WorkflowStatus == SPYWorkflowStatus::Submitted ||
                                SPYBenefitJournalTable.WorkflowStatus == SPYWorkflowStatus::Started ||
                                SPYBenefitJournalTable.WorkflowStatus == SPYWorkflowStatus::RequestChange) || !isWorkflowEnabled) &&
                                !SPYBenefitJournalTable.Posted ?
                                true : false;

    SPYBenefitJournalValidate.enabled(!SPYBenefitJournalTable.Posted);
    SPYBenefitJournalPost.enabled(!SPYBenefitJournalTable.Posted);
    SPYBenefitLedger.enabled(SPYBenefitJournalTable.Posted);
    SPYPayLedgerPostJrnSvcController.enabled(SPYBenefitJournalTable.Posted &&
                                             SPYBenefitJournalTable.ImmediateDisbursement &&
                                             !SPYBenefitJournalTable.PayrollJournal);
    SPYPayrollJournal.enabled(SPYBenefitJournalTable.Posted &&
                              SPYBenefitJournalTable.ImmediateDisbursement &&
                              SPYBenefitJournalTable.PayrollJournal);

    SPYBenefitJournalTable_ds.allowEdit(checkEditable);
    SPYBenefitJournalTable_ds.allowDelete(SPYBenefitJournalTable.WorkflowStatus == SPYWorkflowStatus::Created);
    SPYBenefitJournalTrans_ds.allowCreate(checkEditable);
    SPYBenefitJournalTrans_ds.allowEdit(checkEditable);
    SPYBenefitJournalTrans_ds.allowDelete(checkEditable);

    SPYBenefitJournalTrans_ds.object(fieldNum(SPYBenefitJournalTrans,TransactionCurPnLAmount)).allowEdit(
    SPYBenefitJournalTable.JournalType == SPYBenefitJournalType::Encashment);
    GroupAccruedDetails.visible(SPYBenefitJournalTable.JournalType == SPYBenefitJournalType::Encashment);
}

public int active()
