[Form]
public class SPYBenefitLedger extends FormRun
{
    SPYBenefitJournalTable                  benefitJournalTable;
    QueryBuildRange                         benefitJournalRange;

    boolean                                 EOSRunCaller;
    SPYEOSRunRecId                          EOSRun;
    QueryBuildRange                         EOSRunRange;

    boolean                                 masterProcessCaller;
    HcmWorkerRecId                          worker;
    SPYPayrollPeriodLineRecId               payrollPeriodLine;
    QueryBuildRange                         workerRange;
    QueryBuildRange                         payrollPeriodLineRange;

}

public void init()
{
    SPYEOSRunTable      EOSRunTable;
    FormRun             formRun;
    container           argsCon;

    if(element.args().record() is SPYBenefitJournalTable)
        benefitJournalTable = element.args().record() as SPYBenefitJournalTable;
    else if(element.args().caller() && element.args().record() is SPYEOSRunTable)
    {
        EOSRunTable = element.args().record() as SPYEOSRunTable;
        EOSRun = EOSRunTable.RecId;
        EOSRunCaller = true;
    }
    else if(element.args().caller())
    {
        formRun = element.args().caller();
        if(formRun.name() == formStr(SPYMasterProcess) || formRun.name() == formStr(SPYPayArrearProcess))
        {
            masterProcessCaller = true;
            argsCon = str2con(element.args().parm());
            worker = conPeek(argsCon,1);
            payrollPeriodLine = conPeek(argsCon,2);
        }
    }

    super();
}

public void executeQuery()
{
    if(benefitJournalTable)
        benefitJournalRange.value(queryValue(benefitJournalTable.RecId));
    else if(EOSRunCaller)
        EOSRunRange.value(queryValue(EOSRun));
    if(masterProcessCaller)
    {
        workerRange.value(queryValue(worker));
        payrollPeriodLineRange.value(queryValue(payrollPeriodLine));
    }
    super();
}

public void init()
{
    QueryBuildDataSource    benefitLedgerDS;
    QueryBuildDataSource    benefitJournalTransDS;
    QueryBuildDataSource    EOSBenefitDataSource;

    super();

    if(benefitJournalTable)
    {
        benefitLedgerDS = this.query().dataSourceTable(tableNum(SPYBenefitLedger));
        benefitJournalTransDS = benefitLedgerDS.addDataSource(tableNum(SPYBenefitJournalTrans));
        benefitJournalTransDS.addLink(fieldnum(SPYBenefitLedger,BenefitJournalTrans),fieldNum(SPYBenefitJournalTrans,RecId));
        benefitJournalRange = benefitJournalTransDS.addRange(fieldNum(SPYBenefitJournalTrans,benefitJournal));
    }
    else if(EOSRunCaller)
    {
        EOSBenefitDataSource = this.query().dataSourceTable(tableNum(SPYBenefitLedger)).addDataSource(tableNum(SPYEOSRunBenefit));
        EOSBenefitDataSource.addLink(fieldNum(SPYBenefitLedger,RecId),fieldNum(SPYEOSRunBenefit,BenefitLedger));
        EOSRunRange = EOSBenefitDataSource.addRange(fieldNum(SPYEOSRunBenefit,EOSRun));
    }
    if(masterProcessCaller)
    {
        workerRange = this.query().dataSourceTable(tableNum(SPYBenefitLedger)).addRange(fieldNum(SPYBenefitLedger,Worker));
        payrollPeriodLineRange = this.query().dataSourceTable(tableNum(SPYBenefitLedger)).addRange(fieldNum(SPYBenefitLedger,PayrollPeriodLine));
    }
}

/// <summary>
///
/// </summary>
public void jumpRef()
{
    Args    args = new Args();
    SPYBenefitJournalTable       benefitJournalTableRef = SPYBenefitJournalTrans::find(SPYBenefitLedger.BenefitJournalTrans).benefitJournalTable();
    args.record(benefitJournalTableRef);
    args.lookupRecord(benefitJournalTableRef);

    //super();
    new MenuFunction(menuitemDisplayStr(SPYBenefitJournal), MenuItemType::Display).run(args);
}

