/// <summary>
///
/// </summary>
public void clicked()
{
    Args args = new Args(formstr(SPYExpressionTrace));
    FormRun formRun;
    args.caller(this);
    args.record(SPYExpressionTraceTable::find(SPYBenefitLedger.ExpressionTrace));

    formRun = classFactory.formRunClass(args);
    formRun.init();
    formRun.run();
    formRun.wait();
}

