[Form]
public class SPYDocumentRequest extends FormRun
{
    SPYDocumentRequestTable                 documentRequestTableCaller;
    NumberSeqFormHandler                    numberSeqFormHandler;

}

public boolean canSubmitToWorkflow()
{
    boolean ret;

    ret = SPYDocumentRequestTable.canSubmitToWorkflow();

    return ret;
}

public void init()
{
    if(element.args().record() is SPYDocumentRequestTable)
        documentRequestTableCaller = element.args().record() as SPYDocumentRequestTable;

    super();

    element.setEnable();
}

NumberSeqFormHandler numberSeqFormHandler()
{
    if (!numberSeqFormHandler)
    {
        numberSeqFormHandler = NumberSeqFormHandler::newForm(SPYPayrollParameters::numRefDocumentRequestCode().NumberSequenceId,
                                                             element,
                                                             SPYDocumentRequestTable_ds,
                                                             fieldNum(SPYDocumentRequestTable,DocumentRequestCode)
                                                            );
    }
    return numberSeqFormHandler;
}

void setEnable()
{

    boolean     isWorkflowEnabled = WorkflowTable::findActiveTemplate(workflowtypestr(SPYDocumentRequest)) ? true : false;
    boolean     checkEditable = ((SPYDocumentRequestTable.WorkflowStatus == SPYWorkflowStatus::Created ||
                            SPYDocumentRequestTable.WorkflowStatus == SPYWorkflowStatus::RequestChange) || !isWorkflowEnabled) ?
                            true : false;

    SPYDocumentRequestTable_ds.allowEdit(checkEditable);
    SPYDocumentRequestTable_ds.allowDelete(SPYDocumentRequestTable.WorkflowStatus == SPYWorkflowStatus::Created);
    //SPYDocumentRequestTable_ds.allowCreate(checkEditable);
    SPYDocumentRequestLine_ds.allowEdit(checkEditable);
    SPYDocumentRequestLine_ds.allowDelete(checkEditable);
    SPYDocumentRequestLine_ds.allowCreate(checkEditable);

    this.enableDocRequestGroup();
}

/// <summary>
/// Enables Line fields based on the document request type on Header
/// </summary>
private void enableDocRequestGroup()
{
    SPYDocumentRequestType  documentRequestType = SPYDocumentRequestType::find(SPYDocumentRequestTable.DocumentRequestType);

    switch(documentRequestType.DocRequestType)
    {
        case SPYDocRequestType::EmployeeDisciplinary:
            EmpRequisitionRequest.visible(false);
            EmpTrainingRequest.visible(false);
            EmpDisciplinaryRequest.visible(true);
            EmpDismissalRequest.visible(false);
            break;

        case SPYDocRequestType::EmployeeDismissal:
            EmpRequisitionRequest.visible(false);
            EmpTrainingRequest.visible(false);
            EmpDisciplinaryRequest.visible(false);
            EmpDismissalRequest.visible(true);
            break;

        case SPYDocRequestType::EmployeeRequisition:
            EmpRequisitionRequest.visible(true);
            EmpTrainingRequest.visible(false);
            EmpDisciplinaryRequest.visible(false);
            EmpDismissalRequest.visible(false);
            break;

        case SPYDocRequestType::TrainingRequest:
            EmpRequisitionRequest.visible(false);
            EmpTrainingRequest.visible(true);
            EmpDisciplinaryRequest.visible(false);
            EmpDismissalRequest.visible(false);
            break;

        case SPYDocRequestType::Others:
        case SPYDocRequestType::Blank:
            EmpRequisitionRequest.visible(false);
            EmpTrainingRequest.visible(false);
            EmpDisciplinaryRequest.visible(false);
            EmpDismissalRequest.visible(false);
            break;

        default:
            break;
    }

}

public int active()
{
    int ret;

    ret = super();

    element.setTitleText();
    element.setEnable();

    return ret;
}

public boolean validateWrite()
{
    boolean         ret;

    ret = super();
    ret = element.numberSeqFormHandler().formMethodDataSourceValidateWrite(ret) && ret;

    return ret;
}

