void setEnable()
{
    WorkflowWorkItemTable       workflowWorkItemTable;

    select firstonly RecId from workflowWorkItemTable
        where workflowWorkItemTable.RefTableId == tableNum(SPYLeaveApplicationTable) &&
              workflowWorkItemTable.RefRecId == SPYLeaveApplicationTable.RecId &&
              workflowWorkItemTable.Status == WorkflowWorkItemStatus::Pending &&
              workflowWorkItemTable.UserId == curUserId();

    boolean isEditable, isEditableActualDate;
    if(SPYLeaveApplicationTable.WorkflowStatus == SPYWorkflowStatus::Created)
    {
        if(SPYLeaveApplicationTable.ResumptionInitiated)
        {
            isEditable = false;
            isEditableActualDate = true;
        }
        else
        {
            isEditable = true;
            isEditableActualDate = false;
        }            
    }
    else if(SPYLeaveApplicationTable.WorkflowStatus == SPYWorkflowStatus::Submitted || SPYLeaveApplicationTable.WorkflowStatus == SPYWorkflowStatus::Started)
    {
        if(SPYLeaveApplicationTable.ResumptionInitiated)
        {
            if(workflowWorkItemTable)
                isEditableActualDate = true;
            else
                isEditableActualDate = false;
            isEditable = false;
        }
        else
        {
            if(workflowWorkItemTable)
                isEditable = true;
            else
                isEditable = false;
            isEditableActualDate = false;
        }
    }
    else if(SPYLeaveApplicationTable.WorkflowStatus == SPYWorkflowStatus::Approved)
    {
        if(!SPYLeaveApplicationTable.ResumptionInitiated)
            isEditableActualDate = true;
        else
            isEditableActualDate = false;
        isEditable = false;
    }
    else
    {
        isEditable = false;
        isEditableActualDate = false;
    }

    SPYLeaveApplicationTable_ds.allowDelete(SPYLeaveApplicationTable.WorkflowStatus == SPYWorkflowStatus::Created);
    SPYLeaveApplicationTable_ds.allowEdit(isEditable);
    SPYLeaveApplicationLine_ds.allowCreate(isEditable);
    SPYLeaveApplicationLine_ds.allowDelete(isEditable);
    SPYLeaveApplicationLine_ds.object(fieldNum(SPYLeaveApplicationLine,HRMAbsenceCode)).allowEdit(isEditable);
    SPYLeaveApplicationLine_ds.object(fieldNum(SPYLeaveApplicationLine,ValidFrom)).allowEdit(isEditable);
    SPYLeaveApplicationLine_ds.object(fieldNum(SPYLeaveApplicationLine,ValidTo)).allowEdit(isEditable);
    SPYLeaveApplicationLine_ds.object(fieldNum(SPYLeaveApplicationLine,ActualStartDate)).allowEdit(isEditableActualDate);
    SPYLeaveApplicationLine_ds.object(fieldNum(SPYLeaveApplicationLine,ActualEndDate)).allowEdit(isEditableActualDate);
    SPYLeaveApplicationLine_ds.object(fieldNum(SPYLeaveApplicationLine,Hours)).allowEdit(isEditable &&
                                                                                       (SPYLeaveApplicationLine.ValidFrom == SPYLeaveApplicationLine.ValidTo));
    SPYLeaveApplicationTable_ds.object(fieldNum(SPYLeaveApplicationTable,Worker)).allowEdit(isEditable);
    SPYLeaveResumptionService.enabled(SPYLeaveApplicationTable.WorkflowStatus == SPYWorkflowStatus::Approved &&
                                      !SPYLeaveApplicationTable.ServiceResumed);

}

