public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    SysReferenceTableLookup sysTableLookup = SysReferenceTableLookup::newParameters(tableNum(HcmWorker), _formReferenceControl);
    Query                   lookupQuery;
    QueryBuildDataSource    qbds, qbdsTeamMember;
    HcmWorker               worker;
    SecurityRole            role;
    SecurityUserRole        userRole;
    UserInfo                userInfo;
    SPYPayrollAdminRole     payrollAdmin;

    #define.Name("Name")
    sysTableLookup.addLookupfield(fieldNum(HcmWorker, Personnelnumber));
    sysTableLookup.addLookupMethod(#Name);
    lookupQuery = new Query();
    qbds = lookupQuery.addDataSource(tableNum(HcmWorker));

    select firstonly role
        exists join userRole
            exists join payrollAdmin
            where role.RecId == userRole.SecurityRole
        && role.RecId == payrollAdmin.SecurityRole
        && userRole.User == curUserId();

    if(!role)
    {
        qbdsTeamMember = qbds.addDataSource(tableNum(SPYMyTeamMember));
        qbdsTeamMember.addLink(fieldNum(HcmWorker,RecId),fieldNum(SPYMyTeamMember,TeamMember));
    }
    qbds.addSortField(fieldNum(HcmWorker,Personnelnumber));
    sysTableLookup.parmQuery(lookupQuery);
    worker = sysTableLookup.performFormLookup();

    return worker;
}

