public void executeQuery()
{
    date    fromDate = dateNull();
    date    toDate = dateMax();

    super();
    select validTimeState(fromDate,toDate) SPYLeaveApplicationLine
        where SPYLeaveApplicationLine.LeaveApplication == SPYLeaveApplicationTable.RecId;

}

public void modified()
{
    super();
    if(SPYLeaveApplicationTable.WorkflowStatus == SPYWorkflowStatus::Created)
        SPYLeaveApplicationLine_ds.object(fieldNum(SPYLeaveApplicationLine,Hours)).allowEdit(
                                                                                       SPYLeaveApplicationLine.ValidFrom == SPYLeaveApplicationLine.ValidTo);
    else if(SPYLeaveApplicationTable.WorkflowStatus == SPYWorkflowStatus::Approved && !SPYLeaveApplicationTable.ServiceResumed)
        SPYLeaveApplicationLine_ds.object(fieldNum(SPYLeaveApplicationLine,Hours)).allowEdit(
                                                                                       SPYLeaveApplicationLine.ActualStartDate == SPYLeaveApplicationLine.ActualEndDate);
    else
        SPYLeaveApplicationLine_ds.object(fieldNum(SPYLeaveApplicationLine,Hours)).allowEdit(false);
}

