public void lookup(FormControl _formControl, str _filterStr)
{
    QueryBuildDataSource    configLineDataSource;
    SysTableLookup          sysTableLookup = SysTableLookup::newParameters(tableNum(HRMAbsenceCode), _formControl);
    Query                   query = new Query(queryStr(SPYWorkerAbsenceCodeLookup));

    sysTableLookup.addLookupfield(fieldNum(HRMAbsenceCode, hrmAbsenceCodeId));
    sysTableLookup.addLookupfield(fieldNum(HRMAbsenceCode, description));
    configLineDataSource = query.dataSourceTable(tableNum(SPYWorkerPositionAbsenceConfigLine));
    configLineDataSource.addRange(fieldNum(SPYWorkerPositionAbsenceConfigLine, Worker)).value(queryValue(SPYLeaveApplicationTable.Worker));
    configLineDataSource.addRange(fieldNum(SPYWorkerPositionAbsenceConfigLine, LegalEntity)).value(queryValue(CompanyInfo::current()));
    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

