    boolean     myLoan;

}

public boolean canSubmitToWorkflow()
{
    boolean ret;

    ret = SPYPayLoanTable.canSubmitToWorkflow();

    return ret;
}

public void init()
{
    if(element.args().record() is SPYPayLoanTable)
        payLoanCaller = element.args().record() as SPYPayLoanTable;
    if(element.args().menuItemName() == menuItemDisplayStr(SPYPayLoanListPage_MyLoan))
        myLoan = true;
    super();
    element.setEnable();
}

NumberSeqFormHandler numberSeqFormHandler()
{
    if (!numberSeqFormHandler)
    {
        numberSeqFormHandler = NumberSeqFormHandler::newForm(SPYPayrollParameters::numRefPayLoanCode().NumberSequenceId,
                                                             element,
                                                             SPYPayLoanTable_ds,
                                                             fieldNum(SPYPayLoanTable,PayLoanCode)
                                                            );
    }
    return numberSeqFormHandler;
}

public SPYPayrollParameterState payrollParameterState()
{
    container                           workerContainer;
    SPYPayLoanLine                      payLoanLine;
    SPYPayrollParameterState            payrollParameterState = new SPYPayrollParameterState();
    container                           payrollLedgerCon;
    SPYPayrollLedger                    payrollLedger;

    workerContainer = conPoke(workerContainer,conLen(workerContainer)+1,SPYPayLoanTable.Worker);
    while select RecId from payrollLedger
        join RecId from payLoanLine
        where payLoanLine.RecId == payrollLedger.PayLoanLine &&
              payLoanLine.PayLoan == SPYPayLoanTable.RecId
    {
        payrollLedgerCon = conpoke(payrollLedgerCon,conlen(payrollLedgerCon) + 1,payrollLedger.RecId);
    }
    payrollParameterState.parmPayrollLedgerCon(payrollLedgerCon);
    payrollParameterState.parmWorkerContainer(workerContainer);
    payrollParameterState.parmCalledFromJournal(true);
    payrollParameterState.parmSourceDocument(SPYPayLoanTable.TableId);
    payrollParameterState.parmSourceDocumentRef(SPYPayLoanTable.RecId);
    return payrollParameterState;
}

void refreshDataSource()
{
    SPYPayLoanTable_ds.research(true);
}

void setEnable()
{
    boolean     isWorkflowEnabled = WorkflowTable::findActiveTemplate(workflowtypestr(SPYPayLoanJour)) ? true : false;
    boolean     checkEditable = ((SPYPayLoanTable.WorkflowStatus == SPYWorkflowStatus::Created ||
                                SPYPayLoanTable.WorkflowStatus == SPYWorkflowStatus::Submitted ||
                                SPYPayLoanTable.WorkflowStatus == SPYWorkflowStatus::Started ||
                                SPYPayLoanTable.WorkflowStatus == SPYWorkflowStatus::RequestChange) || !isWorkflowEnabled) &&
                                !SPYPayLoanTable.Posted ?
                                true : false;

    SPYPayLoanTable_ds.allowEdit(checkEditable);
    SPYPayLoanTable_ds.allowDelete(SPYPayLoanTable.WorkflowStatus == SPYWorkflowStatus::Created);
    SPYPayLoanTable_ds.object(fieldNum(SPYPayLoanTable, Worker)).allowEdit(checkEditable && !myLoan);
    SPYPayLoanLine_ds.allowCreate(checkEditable);
    SPYPayLoanLine_ds.allowEdit(checkEditable);
    SPYPayLoanLine_ds.allowDelete(checkEditable);


    SPYPayLoanValidate.enabled(!SPYPayLoanTable.Posted);
    SPYPayLoanPost.enabled(!SPYPayLoanTable.Posted);
    SPYPayLoanLedger.enabled(SPYPayLoanTable.Posted);
    SPYPayLoanCreateInstallments.enabled(SPYPayLoanTable.Posted);
    SPYPayLedgerPostJrnSvcController.enabled(SPYPayLoanTable.Posted);
    SPYPayrollJournal.enabled(SPYPayLoanTable.Posted);
    SPYPayLoanCashSettlement.enabled(SPYPayLoanTable.Posted);
}

public void setTitleText()
{
    headerTitle.text(strFmt('%1 : %2', SPYPayLoanTable.PayLoanCode,
                                       HcmWorker::find(SPYPayLoanTable.Worker).name()));
}

public void executeQuery()
{

    super();
    if(payLoanCaller)
    {
        this.query().dataSourceTable(tableNum(SPYPayLoanTable)).addRange(fieldNum(SPYPayLoanTable,PayLoanCode))
        .value(queryValue(payLoanCaller.PayLoanCode));
    }
}

public void init()
{
    super();

    if(payLoanCaller)
    {
        this.query().dataSourceTable(tableNum(SPYPayLoanTable)).addRange(fieldNum(SPYPayLoanTable,PayLoanCode))
        .value(queryValue(payLoanCaller.PayLoanCode));
    }
}

