/// <summary>
///
/// </summary>
private void insertProposedCompensations(RecId  _promotionWorkerTableRecId)
{
    SPYPromotionWorkerProposedPayroll                   promotionWorkerProposedPayroll;
    SPYPromotionWorkerProposedBenefit                   promotionWorkerProposedBenefit;
    SPYPromotionWorkerProposedAbsenceConfig      promotionWorkerProposedAbsenceConfig;
    SPYCompensationPlanPayroll                                  compensationPlanPayroll;
    SPYCompensationPlanBenefit                              compensationPlanBenefit;
    SPYCompensationPlanTable                                compensationPlanTable;
    SPYPromotionWorkerTable                                 promotionWorkerTable = SPYPromotionWorkerTable::findRecId(_promotionWorkerTableRecId);
    HcmPositionDetail                                               hcmPositionDetail;
    TransDate                                                           validFrom, validTo;

    validFrom = dateNull();
    validTo     = dateMax();

    delete_from promotionWorkerProposedBenefit
        where promotionWorkerProposedBenefit.PromotionWorker == promotionWorkerTable.RecId;

    delete_from promotionWorkerProposedPayroll
        where promotionWorkerProposedPayroll.PromotionWorker == promotionWorkerTable.RecId;

    select validtimestate(validFrom, validTo) * from compensationPlanTable
                    exists join hcmPositionDetail
                where hcmPositionDetail.Position == promotionWorkerTable.ProposedPosition
                            && hcmPositionDetail.SPYCompensationLevelBand == compensationPlanTable.CompensationLevelBand
                            && hcmPositionDetail.SPYCompensationLevelGrade == compensationPlanTable.CompensationLevelGrade
                            && hcmPositionDetail.SPYCompensationLevelStep == compensationPlanTable.CompensationLevelStep;

    while select compensationPlanPayroll
                where compensationPlanPayroll.CompensationPlan == promotionWorkerTable.RecId
    {
        promotionWorkerProposedPayroll.Monthly                 = compensationPlanPayroll.MonthlyMax;
        promotionWorkerProposedPayroll.Payroll                  = compensationPlanPayroll.Payroll;
        promotionWorkerProposedPayroll.PromotionWorker  = promotionWorkerTable.RecId;
        promotionWorkerProposedPayroll.insert();
    }

    while select compensationPlanBenefit
                where compensationPlanBenefit.CompensationPlan == compensationPlanTable.RecId
    {
        promotionWorkerProposedBenefit.AmountAnnual     = compensationPlanBenefit.AmountAnnualMax;
        promotionWorkerProposedBenefit.QtyMonthly       = compensationPlanBenefit.QtyQuarterlyMax;
        promotionWorkerProposedBenefit.QtyAnnual        = compensationPlanBenefit.QtyAnnualMax;
        promotionWorkerProposedBenefit.Benefit              = compensationPlanBenefit.Benefit;
        promotionWorkerProposedBenefit.PromotionWorker = promotionWorkerTable.RecId;
        promotionWorkerProposedBenefit.insert();
    }

    promotionWorkerProposedAbsenceConfig.AbsenceConfig          = compensationPlanTable.AbsenceConfig;
    promotionWorkerProposedAbsenceConfig.PromotionWorkerTable   = promotionWorkerTable.RecId;
    promotionWorkerProposedAbsenceConfig.Worker                 = promotionWorkerTable.Worker;
    promotionWorkerProposedAbsenceConfig.insert();

}

/// <summary>
///
/// </summary>
/// <returns></returns>
public int active()
{
    int ret;

    ret = super();
    element.setEnable();

    return ret;
}
