[Form]
public class SPYWorkerTransferRequest extends FormRun
{
    NumberSeqFormHandler                numberSeqFormHandler;
    boolean     myRequest, transferInRequest, transferOutRequest;

}

public boolean canSubmitToWorkflow()
{
    boolean ret;

    ret = SPYWorkerTransferRequest.canSubmitToWorkflow();

    return ret;
}

public void close()
{
    if (numberSeqFormHandler)
    {
        numberSeqFormHandler.formMethodClose();
    }
    super();
}

NumberSeqFormHandler numberSeqFormHandler()
{
    if (!numberSeqFormHandler)
    {
        numberSeqFormHandler = NumberSeqFormHandler::newForm(SPYPayrollParameters::numRefWorkerTransferRequestCode().NumberSequenceId,
                                                             element,
                                                             SPYWorkerTransferRequest_ds,
                                                             fieldNum(SPYWorkerTransferRequest, workerTransferRequestCode)
                                                            );
    }
    return numberSeqFormHandler;
}

public void init()
{
    super();
    if(element.args().menuItemName() == menuItemDisplayStr(SPYWorkerTransferRequestList_MyRequest))
        myRequest = true;
    else if(element.args().menuItemName() == menuItemDisplayStr(SPYWorkerTransferRequestList_AllTransferInRequest))
        transferInRequest = true;
    else if(element.args().menuItemName() == menuItemDisplayStr(SPYWorkerTransferRequestList_AllTransferOutRequest))
        transferOutRequest = true;
}

public void setEnable()
{
    boolean editable;

    if(SPYWorkerTransferRequest.WorkflowStatus == SPYWorkflowStatus::Created)
        editable = true;
    else
        editable = false;
    SPYWorkerTransferRequest_ds.allowEdit(editable);
    SPYWorkerTransferRequest_ds.allowDelete(editable);   
    SPYWorkerTransferRequestEmploymentDetailsTo_ds.allowCreate(editable);
    SPYWorkerTransferRequestEmploymentDetailsTo_ds.allowEdit(editable);
    SPYWorkerTransferRequestEmploymentDetailsTo_ds.allowDelete(editable);
    SPYWorkerTransferRequestPayrollTo_ds.allowCreate(editable);
    SPYWorkerTransferRequestPayrollTo_ds.allowEdit(editable);
    SPYWorkerTransferRequestPayrollTo_ds.allowDelete(editable);
    SPYWorkerTransferRequestBenefitTo_ds.allowCreate(editable);
    SPYWorkerTransferRequestBenefitTo_ds.allowEdit(editable);
    SPYWorkerTransferRequestBenefitTo_ds.allowDelete(editable);
    SPYWorkerTransferRequestAbsenceConfigTo_ds.allowCreate(editable);
    SPYWorkerTransferRequestAbsenceConfigTo_ds.allowEdit(editable);
    SPYWorkerTransferRequestAbsenceConfigTo_ds.allowDelete(editable);

    RefreshTransferFrom.enabled(editable && SPYWorkerTransferRequest.RecId);
    PostButton.enabled(SPYWorkerTransferRequest.WorkflowStatus == SPYWorkflowStatus::Approved &&
        !SPYWorkerTransferRequest.Posted && SPYWorkerTransferRequest.RecId);

    if(myRequest)
    {
        SPYWorkerTransferRequest_ds.object(fieldNum(SPYWorkerTransferRequest, Worker)).allowEdit(false);
    }
    else if(transferInRequest)
    {
        TransferFromTabPage.visible(false);
    }
}

public boolean validateWrite()
{
    boolean         ret;

    ret = super();
    ret = element.numberSeqFormHandler().formMethodDataSourceValidateWrite(ret) && ret;

    return ret;
}

public int active()
{
    int ret;

    ret = super();
    element.setTitleText();
    element.setEnable();
    return ret;
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    Query query = new Query();
    QueryBuildDataSource queryBuildDataSource;
    QueryBuildRange queryBuildRange;

    SysReferenceTableLookup sysTableLookup = SysReferenceTableLookup::newParameters(tableNum(CompanyInfo), _formReferenceControl, true);

    sysTableLookup.addLookupfield(fieldNum(CompanyInfo, DataArea));
    sysTableLookup.addLookupfield(fieldNum(CompanyInfo, Name));

    queryBuildDataSource = query.addDataSource(tableNum(CompanyInfo));
    QueryBuildDataSource    employmentDS = queryBuildDataSource.addDataSource(tableNum(HcmEmployment));
    employmentDS.addLink(fieldNum(CompanyInfo, RecId), fieldNum(HcmEmployment, LegalEntity));
    employmentDS.addRange(fieldNum(HcmEmployment, Worker)).value(queryValue(SPYWorkerTransferRequest.Worker));
    if(SPYWorkerTransferRequest.TransferDate)
    {
        query.validTimeStateAsOfDateTime(DateTimeUtil::newDateTime(SPYWorkerTransferRequest.TransferDate, 00-00));
    }

    sysTableLookup.parmQuery(query);

    return sysTableLookup.performFormLookup();
}

public void clicked()
{
    super();
    if(Box::confirm("@SPY:WantToRefreshTansferOut") == true)
    {
        SPYWorkerTransferRequestService  workerTransferRequestService = new SPYWorkerTransferRequestService(SPYWorkerTransferRequest);
        if(Box::confirm("@SPY:WantToRefreshTansferIn") == true)
        {
            workerTransferRequestService.refreshRequestData(true);
        }
        else
        {
            workerTransferRequestService.refreshRequestData(false);
        }
        SPYWorkerTransferRequestEmploymentDetailsFrom_ds.executeQuery();
        SPYWorkerTransferRequestPayrollFrom_ds.executeQuery();
        SPYWorkerTransferRequestBenefitFrom_ds.executeQuery();
        SPYWorkerTransferRequestAbsenceConfigFrom_ds.executeQuery();
        SPYWorkerTransferRequestBenefitFromBalance_ds.executeQuery();
        SPYWorkerTransferRequestPositionAssignmentFrom_ds.executeQuery();
        SPYWorkerTransferRequestEmploymentDetailsTo_ds.executeQuery();
        SPYWorkerTransferRequestPayrollTo_ds.executeQuery();
        SPYWorkerTransferRequestBenefitTo_ds.executeQuery();
        SPYWorkerTransferRequestAbsenceConfigTo_ds.executeQuery();
    }            
}

public void clicked()
{
    super();
    if(Box::confirm("@SPY:WantToPostTransferRequest") == true)
    {
        SPYWorkerTransferRequestService  workerTransferRequestService = new SPYWorkerTransferRequestService(SPYWorkerTransferRequest);
        workerTransferRequestService.updateWorkerDetails();
        SPYWorkerTransferRequest_ds.reread();
        SPYWorkerTransferRequest_ds.refresh();
    }
}
